// populate dropdown list of department names and values
function PopulateDeptList() {
  var deptSelect = document.getElementById("deptSelect");

  for (var i = 0; i < deptList.length; i++) {
    var option = document.createElement("OPTION");
    option.innerHTML = deptList[i].deptname;
    option.value = deptList[i].dept;
    deptSelect.options.add(option);
  }
}

// get selected dept and sem data , save to session and navigate to next page
function getData()
{
  var deptname = document.getElementById("deptSelect").value;
  var semester = document.getElementById("semSelect").value;

  sessionStorage.setItem("deptSelect", deptname);
  sessionStorage.setItem("semSelect", semester);

  location.href = "calc.html";

}

